import React, { Component } from 'react';
import {connect} from 'react-redux';

class ViewPaymentMeth extends Component{
    render(){
        debugger;
        if(this.props.requestState)
        {

        }
        if( this.props.requestState.apiInProcess )
        {
            return(
                <h1>Loading Data</h1>
            );
        }
        else if(this.props.requestState.status != 200)
        {
            return(
                <h1>Error fetching data</h1>
            );
        }
        else
        {
            return(
                this.props.paymentDataArr.forEach(item => {
                    return(
                        <h1>
                            {item.modetype} - {item.modesubtype}
                        </h1>
                    );

                })
            );
        }
    }
}

const mapStateToProps = (state) => {
    return {
        paymentDataArr : state.paymentData,
        requestState : state.apiRequest
    }
}

export default connect(mapStateToProps)(ViewPaymentMeth);
