import React, { Component } from 'react';
import {connect} from 'react-redux';
import TableComponent from '../../UI/Table/Table';


const tableComponentData = {
    tableConfig : {
        actions : [
            {
                name : 'Edit',
                click : 'onClickHander()'
            }
        ]
    },
    tableData : [
                    {modetype:'Debit Card' , modesubtype:'ICICI'},
                    {modetype:'Debit Card' , modesubtype:'BOB'}
                ],
    tableRows : ['Payment Mode' , 'Payment Sub Mode']
}
class ViewPaymentMeth extends Component{
    render(){
        debugger;
        if(this.props.requestState)
        {
            if( this.props.requestState.apiInProcess === true)
            {
                return(
                    <h1>Loading Data</h1>
                    );
                }
                else if(this.props.requestState.status != 200)
                {
                    return(
                        <h1>Error fetching data</h1>
                        );
                    }
                    else
                    {
                        if( this.props.paymentDataArr.paymentModeArr.length > 0 )
                        {
                            tab
                            return(<TableComponent tableComponentData={tableComponentData} />);
                    return (
                        <ul>
                            {this.props.paymentDataArr.paymentModeArr.map((item, index) => {
                                return(
                                    <h1 key={index}>
                                        {item.modetype} - {item.modesubtype} 
                                    </h1> 
                                );
                              })}
                        </ul>
                    )

                }
                else
                {
                    return(<h1>No payment modes defined</h1>);
                }

            }
        }
        else
        {
            return(<h1>Loading...</h1>);
        }
    }
}

const mapStateToProps = (state) => {
    debugger;
    return {
        paymentDataArr : state.PaymentReducer,
        requestState : state.ApiReducer,

    }
}

export default connect(mapStateToProps)(ViewPaymentMeth);
