import React , { useState , useEffect} from 'react';
import Toast from 'react-bootstrap/Toast'
import ToastHeader from 'react-bootstrap/ToastHeader'
import ToastBody from 'react-bootstrap/ToastBody'
import './Toast.scss';
// const toggleToast = () => {
//     toggleToastFlag = !toggleToastFlag;
// }

const ToastComponent = ( props ) => {
    const [toastState , toggleToast] = useState({
        toggleToastFlag : false
    });

    const closeToastHandler = (  ) => {
        toggleToast({
            toggleToastFlag : false
        });
    }
    useEffect(() => {
        console.log("Called Toast Update" , toastState);
        // if( props.showToast === true )
        // {
        //     toggleToast(
        //         {
        //             toggleToastFlag : true
        //         }
        //     );
        // }
      });

    return(
        <Toast show={props.showToast}  onClose={closeToastHandler} autohide delay={1000}>
            <Toast.Header>
              <img
                src="holder.js/20x20?text=%20"
                className="rounded mr-2"
                alt=""
              />
              <strong className="mr-auto">Bootstrap</strong>
              <small>11 mins ago</small>
            </Toast.Header>
            <Toast.Body>
              Woohoo, you're reading this text in a Toast!
            </Toast.Body>
          </Toast>
    );

}

export default ToastComponent;
