import React from 'react';
import Table from 'react-bootstrap/Table'

const tableComponentData = {
    tableConfig : {
        actions : [
            {
                name : 'Edit',
                click : 'editItem'
            }
        ]
    },
    tableData : [
                    {modetype:'Debit Card' , modesubtype:'ICICI'},
                    {modetype:'Debit Card' , modesubtype:'BOB'}
                ],
    tableRows : ['Payment Mode' , 'Payment Sub Mode']
}

const TableComponent = ( props ) => {
    debugger
    const { tableConfig , tableData , tableRows } = tableComponentData;

    let theads = tableRows.map( (item,index)=>{
        return(<th key={index}>
                {item}
            </th>);
    });

    let tdata = tableData.map( (item,index)=>{

            let tdArr = [];
            let ind = 0;
            for (var key in item) {
                tdArr.push( <td key={index+'_'+ind}> {item[key]} </td>  );
                ind++;
            }
        return(
               {tdArr}
            );
    });

    if( props.tableData.tableData.length > 0 )
    {
        return(<Table responsive>
                    <thead>
                        <tr>
                            {theads}
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {tdata}
                        </tr>
                    </tbody>
                </Table>);
    }
    else
    {
        return <h1>Nothing to show here</h1>
    }



}

export default TableComponent
