            }
        ]
    },
    tableData : [
                    {modetype:'Debit Card' , modesubtype:'ICICI'},
                    {modetype:'Debit Card' , modesubtype:'BOB'}
                ],
    tableRows : ['Payment Mode' , 'Payment Sub Mode','Actions']
}

const TableComponent = ( props ) => {
    debugger
